function getCameras(){return fetch("http://localhost:3000/api/cameras").then((function(response){return response.json()})).then((function(cameras){return cameras})).catch((function(error){alert(error)}))}function displayCamera(camera){const templateElt=document.getElementById("templateCamera"),cloneElt=document.importNode(templateElt.content,!0);cloneElt.getElementById("imageUrl"),cloneElt.getElementById("ref"),cloneElt.getElementById("name"),cloneElt.getElementById("description"),cloneElt.getElementById("lenses"),cloneElt.getElementById("price"),document.getElementById("bloc__image1").appendChild(cloneElt)}!async function(){const cameras=await getCameras();for(camera of cameras)displayCamera(camera),console.log(camera)}();